import { Component, ViewChild } from '@angular/core';
import { Student } from '../models/student';
import { MatTable } from '@angular/material/table';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { trigger, state, style } from '@angular/animations';


@Component({
  selector: 'app-student-management',
  templateUrl: './student-management.component.html',
  styleUrls: ['./student-management.component.css'],
  animations: [
    trigger(
      'visibilityChanged', [
        state('shown', style({opacity: 1})),
        state('hidden', style({opacity: 0}))
      ])
  ]
})
export class StudentManagementComponent {
  // @ts-ignore
  @ViewChild(MatTable) table: MatTable<Student>;

  public visibilityChanged: string = 'hidden';

  public newStudentForm: FormGroup = new FormGroup({
    name: new FormControl('', [Validators.maxLength(255), Validators.required]),
    age: new FormControl(null, [Validators.min(12), Validators.required]),
    course: new FormControl('', Validators.required)
  });

  public students: Student[] = [
    { name: 'Peter Parker', age: 16, course: 'Science' },
    { name: 'Miles Morales', age: 16, course: 'Arts' },
    { name: 'Gwen Stacy', age: 16, course: 'Music' },
  ];

  public displayedColumns: string[] = ['name', 'age', 'course', 'action'];

  public loadAddNewStudentForm(): void {

  }

  public addNewStudent(): void {
    this.students.push(this.newStudentForm.value);
    this.table.renderRows();
    this.newStudentForm.reset();
  }

  public findStudentByName(studentName: string): Student[] {
    return [];
  }

  public removeStudent(studentName: string): void {
    console.log(studentName);
  }
}
<div style="margin: 10px; padding: 5px;">
  <h1>Student Managment</h1>
  <h3>Student list</h3>

  <button mat-raised-button color="primary" (click)="loadAddNewStudentForm()" style="margin-bottom: 10px;">
    Add Student
  </button>

  <mat-card [visibility]="visibility ? 'shown' : 'hidden'">
    <mat-card-header>
      <mat-card-title style="color: #fff">Add New Student Form</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <form [formGroup]="newStudentForm"
        style="display: flex; align-items: start; justify-content: start; flex-direction: column;">
        <mat-form-field appearance="fill">
          <mat-label>Name</mat-label>
          <input matInput type="text" formControlName="name" placeholder="Ex: Denise Pereira">
        </mat-form-field>
        <mat-form-field appearance="fill">
          <mat-label>Age</mat-label>
          <input matInput type="number" name="age" id="age" formControlName="age" placeholder="12+">
        </mat-form-field>
        <mat-form-field appearance="fill">
          <mat-label>Course</mat-label>
          <input matInput type="text" name="course" id="course" formControlName="course" placeholder="Ex: Science">
        </mat-form-field>
      </form>
      <button mat-raised-button color="primary" type="submit" (click)="addNewStudent()">Add New Student</button>
    </mat-card-content>
  </mat-card>

  <table mat-table [dataSource]="students" class="mat-elevation-z8">
    <!-- Name column -->
    <ng-container matColumnDef="name">
      <th mat-header-cell *matHeaderCellDef>Name</th>
      <td mat-cell *matCellDef="let student">{{student.name}}</td>
    </ng-container>


    <!-- Age column -->
    <ng-container matColumnDef="age">
      <th mat-header-cell *matHeaderCellDef>Age</th>
      <td mat-cell *matCellDef="let student">{{student.age}}</td>
    </ng-container>


    <!-- Course column -->
    <ng-container matColumnDef="course">
      <th mat-header-cell *matHeaderCellDef>Course</th>
      <td mat-cell *matCellDef="let student">{{student.course}}</td>
    </ng-container>

    <!-- Action column -->
    <ng-container matColumnDef="action">
      <th mat-header-cell *matHeaderCellDef> Action </th>
      <td mat-cell *matCellDef="let student">
        <ng-container>
          <button type="button" (click)="removeStudent(student.name)" mat-mini-fab color="warn">
            <mat-icon>delete</mat-icon>
          </button>
        </ng-container>
      </td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>
</div>
