import { Component, ViewChild } from '@angular/core';
import { Student } from '../models/student';
import { MatTable } from '@angular/material/table';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { trigger, state, style, transition, animate } from '@angular/animations';


@Component({
  selector: 'app-student-management',
  templateUrl: './student-management.component.html',
  styleUrls: ['./student-management.component.css'],
  animations: [
    trigger(
      'visibilityChanged', [
      state('shown', style({ opacity: 1 })),
      state('hidden', style({ opacity: 0 })),
      transition('shown => hidden', animate('.30s ease-out', style({ height: 0 })))
    ]),
    trigger(
      'searchVisibility', [
        state('open', style({opacity: 1})),
        state('closed', style({opacity: 0})),
        transition('open => close', animate('.5s ease-in-out'))
      ]
    )
  ]
})
export class StudentManagementComponent {
  // @ts-ignore
  @ViewChild(MatTable) table: MatTable<Student>;

  public visibilityChanged: string = 'hidden';
  public searchVisibilityChanged: string = 'close';


  public newStudentForm: FormGroup = new FormGroup({
    name: new FormControl('', [Validators.maxLength(255), Validators.required]),
    age: new FormControl(null, [Validators.min(12), Validators.required]),
    course: new FormControl('', Validators.required)
  });

  public students: Student[] = [
    { id: 1, name: 'Peter Parker', age: 16, course: 'Science' },
    { id: 2, name: 'Miles Morales', age: 16, course: 'Arts' },
    { id: 3, name: 'Gwen Stacy', age: 16, course: 'Music' },
  ];

  private studentsAux: Student[] = [
    { id: 1, name: 'Peter Parker', age: 16, course: 'Science' },
    { id: 2, name: 'Miles Morales', age: 16, course: 'Arts' },
    { id: 3, name: 'Gwen Stacy', age: 16, course: 'Music' },
  ];

  public displayedColumns: string[] = ['name', 'age', 'course', 'action'];

  public loadAddNewStudentForm(): void {
    this.visibilityChanged = 'shown';
  }

  public addNewStudent(): void {
    this.students.push({
      id: this.students[this.students.length - 1].id + 1,
      name: this.newStudentForm.get('name')?.value,
      age: this.newStudentForm.get('age')?.value,
      course: this.newStudentForm.get('course')?.value
    });
    this.studentsAux = this.students;
    this.table.renderRows();
    this.newStudentForm.reset();
  }

  public onKeyUp(value: string): void {
    this.students = this.findStudentByName(value);
    this.table.renderRows();
  }

  public findStudentByName(studentName: string): Student[] {
    if (studentName === null || studentName.trim().length === 0) {
      return this.studentsAux;
    }

    for (let i = 0; i < this.students.length; i++) {
      if (this.students[i].name.includes(studentName)) {
        return [this.students[i]];
      }
    }
    return [];
  }

  public removeStudent(studentId: number): void {
    this.students = this.students.filter(a => a.id !== studentId);
    this.studentsAux = this.students;

    this.table.renderRows();
  }
}
<div style="margin: 10px; padding: 5px;">
  <h1>Student Managment</h1>
  <h3>Student list</h3>

  <div style="display: flex; justify-content: space-between; width: 30%;">
    <button mat-raised-button color="primary" *ngIf="visibilityChanged === 'hidden'" (click)="loadAddNewStudentForm()"
      style="margin-bottom: 10px;">
      Add Student
    </button>

    <button mat-raised-button color="primary" *ngIf="searchVisibility === 'open'" (click)="loadAddNewStudentForm()"
      style="margin-bottom: 10px;">
      Search
    </button>
  </div>

  <mat-card>
    <mat-card-header>
      <mat-card-title>Search Student By Name</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <div>
        <mat-form-field appearance="fill">
          <mat-label>Search</mat-label>
          <input matInput #search type="text" (keyup)="onKeyUp(search.value)">
        </mat-form-field>
      </div>
    </mat-card-content>
  </mat-card>

  <mat-card *ngIf="visibilityChanged ==='shown'">
    <mat-card-header>
      <mat-card-title style="color: #fff">Add New Student Form</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <form [formGroup]="newStudentForm" style="display: flex; align-items: start; justify-content: start; flex-direction: column;">
        <mat-form-field appearance="fill">
          <mat-label>Name</mat-label>
          <input matInput type="text" formControlName="name" placeholder="Ex: Denise Pereira">
        </mat-form-field>
        <mat-form-field appearance="fill">
          <mat-label>Age</mat-label>
          <input matInput type="number" name="age" id="age" formControlName="age" placeholder="12">
          <mat-hint style="color: #fff;">Age cannot be less than 12 years old</mat-hint>
        </mat-form-field>
        <mat-form-field appearance="fill">
          <mat-label>Course</mat-label>
          <input matInput type="text" name="course" id="course" formControlName="course" placeholder="Ex: Science">
        </mat-form-field>
      </form>
      <div style="display: flex; justify-content: space-between; width: 30%;">
        <button mat-raised-button color="primary" type="submit" (click)="addNewStudent()">Add New Student</button>
        <button mat-raised-button color="primary" type="submit" (click)="visibilityChanged = 'hidden'">Cancel</button>
      </div>
    </mat-card-content>
  </mat-card>

  <table mat-table [dataSource]="students" class="mat-elevation-z8 mt-10">
    <!-- Name column -->
    <ng-container matColumnDef="name">
      <th mat-header-cell *matHeaderCellDef>Name</th>
      <td mat-cell *matCellDef="let student">{{student.name}}</td>
    </ng-container>


    <!-- Age column -->
    <ng-container matColumnDef="age">
      <th mat-header-cell *matHeaderCellDef>Age</th>
      <td mat-cell *matCellDef="let student">{{student.age}}</td>
    </ng-container>


    <!-- Course column -->
    <ng-container matColumnDef="course">
      <th mat-header-cell *matHeaderCellDef>Course</th>
      <td mat-cell *matCellDef="let student">{{student.course}}</td>
    </ng-container>

    <!-- Action column -->
    <ng-container matColumnDef="action">
      <th mat-header-cell *matHeaderCellDef> Action </th>
      <td mat-cell *matCellDef="let student">
        <ng-container>
          <button type="button" (click)="removeStudent(student.id)" mat-mini-fab color="warn">
            <mat-icon>delete</mat-icon>
          </button>
        </ng-container>
      </td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
