import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  public value: number = 0;
  public warnMsg: boolean = false;

  public increment(): void {
    this.warnMsg = false;
    this.value++;
  }

  public reset(): void {
    this.warnMsg = false;
    this.value = 0;
  }

  public decrease(): void {
    if (this.value !== 0) {
      this.value--;
    } else {
      this.warnMsg = true;
    }
  }
}
<button type="button" (click)="increment()">Click Me!</button>
<button type="reset" (click)="reset()">I Set things back to initial value</button>
<button type="button" (click)="decrease()">I decrease values by 1!</button>

<p>The button "Click me" increments the value below by 1</p>
<p>{{value}}</p>

<p *ngIf="!warnMsg" class="danger">
  We cannot decrease from zero.
</p>
